mapper.select:
SELECT NSEQ,NTITLE,NCONTENT,NDATE,NHIT
FROM
(SELECT NSEQ,NTITLE,NCONTENT,NDATE,NHIT,
ROW_NUMBER() OVER(ORDER BY NSEQ DESC) BSJNOTICE_SEQ FROM BSJNOTICE

)
WHERE BSJNOTICE_SEQ BETWEEN #{param1} AND #{param2}

mapper.update:
UPDATE BSJNOTICE
SET NTITLE=#{param1},NCONTENT=#{param2}
WHERE NSEQ=#{param3}
